        }

        /* '' */
        .weui-icon-warn:before {
            content: "\EA0B";
        }

        /* '' */
        .weui-icon-info-circle:before {
            content: "\EA0C";
        }

        /* '' */
        .weui-icon-cancel:before {
            content: "\EA0D";
        }

        /* '' */
        .weui-icon-search:before {
            content: "\EA0E";
        }

        /* '' */
        .weui-icon-clear:before {
            content: "\EA0F";
        }

        /* '' */
        .weui-icon-back:before {
            content: "\EA10";
        }

        /* '' */
        .weui-icon-delete:before {
            content: "\EA11";
        }

        [class^="weui-icon_"]:before,
        [class*=" weui-icon_"]:before {
            margin: 0;
        }

        .weui-icon-success {
            font-size: 23px;
            color: #09BB07;
